import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Users from './Users';

const UsersList = () => {
    // const [users, setUsers] = useState([]); // Initialize as empty array
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [loading, setLoading] = useState(true);
    const limit = 5; // Limit of users per page

    // Function to fetch users
    const fetchUsers = async (page) => {
        try {
            const response = await axios.get(`http://localhost:3001/?page=${page}&limit=${limit}`);
            console.log(response.data); // Log the response data
            // setUsers(response.data.users); // Set users state
            setTotalPages(response.data.totalPages); // Set total pages state
            setLoading(false); // Set loading to false
        } catch (error) {
            console.error("Error fetching users:", error);
            setLoading(false); // Set loading to false on error
        }
    };

    useEffect(() => {
        fetchUsers(currentPage);
    }, [currentPage]);

    // Function to handle page change
    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Users />
            <div>
                <button 
                    disabled={currentPage === 1} 
                    onClick={() => handlePageChange(currentPage - 1)}
                >
                    Previous
                </button>
                {Array.from({ length: totalPages }, (_, index) => (
                    <button 
                        key={index + 1} 
                        onClick={() => handlePageChange(index + 1)} 
                        disabled={currentPage === index + 1}
                    >
                        {index + 1}
                    </button>
                ))}
                <button 
                    disabled={currentPage === totalPages} 
                    onClick={() => handlePageChange(currentPage + 1)}
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default UsersList;
