import axios from "axios";
import { useEffect, useState } from "react";
import Papa from 'papaparse';
import { Link } from "react-router-dom";

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedFile, setSelectedFile] = useState(null); // State to hold the selected file

    useEffect(() => {
        setLoading(true);
        axios.get('https://merncrudbackend-1zpv.onrender.com')
            .then(result => {
                setUsers(result.data);
                setLoading(false);
            })
            .catch(err => {
                console.log(err);
                setLoading(false);
            });
    }, []);

    const handleDelete = (id) => {
        axios.delete('https://merncrudbackend-1zpv.onrender.com/deleteUser/' + id)
            .then(res => {
                setUsers(users.filter(user => user._id !== id)); // Update state after deletion
            })
            .catch(err => console.log(err));
    };

    const handleExport = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/exportUsers', {
                responseType: 'blob', // Important for downloading files
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'export.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting data', error);
        }
    };

    const handleImport = async (file) => {
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('http://localhost:3001/api/importCsv', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Error importing CSV: ' + response.statusText);
            }

            const data = await response.text();
            console.log('Import success:', data); // Log response for debugging
        } catch (error) {
            console.error('Import error:', error.message);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0]; // Access the first file from the input
        setSelectedFile(file); // Save the selected file to state
        console.log('File selected:', file); // Log the file for debugging
    };

    const handleFileUpload = (event) => {
        event.preventDefault();
        if (selectedFile) {
            Papa.parse(selectedFile, {
                header: true, // If the CSV file has headers
                complete: function (results) {
                    console.log('Parsed CSV Data:', results.data); // Parsed data from CSV
                    setUsers(prevUsers => [...prevUsers, ...results.data]); // Add imported data to the existing user list
                    handleImport(selectedFile); // Send file to backend only when Import button is clicked
                },
                error: function (error) {
                    console.error('Error parsing CSV:', error);
                }
            });
        } else {
            console.error('No file selected for import');
            alert('Please select a file before importing.');
        }
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                {/* Button for exporting CSV */}
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                {/* Form for importing CSV */}
                <div className="m-2">
                    <input 
                        type="file" 
                        id="csvFileInput" 
                        accept=".csv" 
                        className="m-2" 
                        onChange={handleFileChange}
                    />
                    <button className="btn btn-warning" onClick={handleFileUpload}>Import CSV</button>
                </div>
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map((user, index) => (
                                    <tr key={index}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
