import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Users = () => {
    const [users, setUsers] = useState([]);
    const [file, setFile] = useState(null);

    // Function to fetch users from the database
    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/');
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    useEffect(() => {
        fetchUsers(); // Fetch users on component mount
    }, []);

    // Handle file input change
    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    // Handle file upload
    const handleImport = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('file', file);

        try {
            await axios.post('http://localhost:3001/api/importCsv', formData);
            alert('File imported successfully'); // Show success message
            fetchUsers(); // Re-fetch users to update the list
        } catch (error) {
            console.error('Error importing file:', error);
            alert('Error importing file');
        }
    };

    return (
        <div>
            <h1>User Management</h1>
            <form onSubmit={handleImport}>
                <input type="file" onChange={handleFileChange} accept=".csv" required />
                <button type="submit">Import CSV</button>
            </form>
            <ul>
                {users.map(user => (
                    <li key={user._id}>{user.name} - {user.email} - {user.age}</li>
                ))}
            </ul>
        </div>
    );
};

export default Users;
