import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { CSVImporter } from "csv-import-react"; // Import CSVImporter

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isOpen, setIsOpen] = useState(false); // State for controlling the modal

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        setLoading(true);
        try {
            const response = await axios.get('https://merncrudbackend-1zpv.onrender.com/api/users');
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = (id) => {
        axios.delete('https://merncrudbackend-1zpv.onrender.com/api/deleteUser/' + id)
            .then(res => {
                setUsers(users.filter(user => user._id !== id));
            })
            .catch(err => console.log(err));
    };

    const handleExport = async () => {
        try {
            const response = await axios.get('https://merncrudbackend-1zpv.onrender.com/api/exportUsers', {
                responseType: 'blob',
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'export.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting data', error);
        }
    };

    const handleImport = async (data) => {
        console.log(data);
        data.forEach(user => {
            axios.post('https://merncrudbackend-1zpv.onrender.com/api/createUser', user)
                .then(() => fetchUsers())
                .catch(error => console.error('Error importing user:', error));
        });
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0]; // Access the first file
        if (file) {
            // Handle file selection (e.g., send to backend)
            console.log('File selected:', file);
        } else {
            console.error('No file selected');
        }
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                <button onClick={() => setIsOpen(true)} className="btn btn-warning m-2">CSV Import</button>
                
                <CSVImporter
                    modalIsOpen={isOpen}
                    modalOnCloseTriggered={() => setIsOpen(false)}
                    darkMode={true}
                    onComplete={handleImport}
                    template={{
                        columns: [
                            {
                                name: "First Name",
                                key: "first_name",
                                required: true,
                                description: "The first name of the user",
                                suggested_mappings: ["First", "Name"],
                            },
                            {
                                name: "Age",
                                data_type: "number",
                            },
                        ],
                    }}
                />
                
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
