import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CSVImporter } from 'react-csv-importer'; // Ensure you have this installed
import axios from 'axios';

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/users'); // Adjust this URL if necessary
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
            } finally {
                setLoading(false);
            }
        };
        fetchUsers();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await axios.delete(`http://localhost:3001/api/users/${id}`);
                setUsers(users.filter(user => user._id !== id));
            } catch (error) {
                console.error('Error deleting user:', error);
            }
        }
    };

    const handleFileChange = (event) => {
        const files = event.target.files;
        // Process files as needed, e.g., read the CSV or XLSX
        console.log(files); // You can log the selected files for debugging
    };

    const handleImport = async (data) => {
        // Handle import logic here
        console.log('Imported Data:', data);
        // You might want to post this data to your backend
    };

    const handleExport = async () => {
        // Handle export logic here
        console.log('Exporting CSV');
        // You might want to trigger a download or make an API call
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                <button onClick={() => setIsOpen(true)} className="btn btn-warning m-2">CSV Import</button>
                
                <input 
                    type="file" 
                    onChange={handleFileChange} // Call handleFileChange here
                    accept=".csv, .xlsx" // Optional: Limit accepted file types
                />
                
                <CSVImporter
                    modalIsOpen={isOpen}
                    modalOnCloseTriggered={() => setIsOpen(false)}
                    darkMode={true}
                    onComplete={handleImport}
                    template={{
                        columns: [
                            {
                                name: "First Name",
                                key: "first_name",
                                required: true,
                                description: "The first name of the user",
                                suggested_mappings: ["First", "Name"],
                            },
                            {
                                name: "Age",
                                data_type: "number",
                            },
                        ],
                    }}
                />
                
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
