import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const UpdateUser = () => {

    const { id } = useParams();
    const [name, setName] = useState();
    const [email, setEmail] = useState();
    const [age, setAge] = useState();
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('https://merncrudbackend-1zpv.onrender.com/getUser/' + id)
            .then(result => {
                console.log("User data fetched:", result.data);
                setName(result.data.name || "")
                setEmail(result.data.email || "")
                setAge(result.data.age || "")
            })
            .catch(err => console.error("Error fetching user:", err));
    },[id]);

    const isValidInput = (value) => {
        return typeof value === "string" && value.trim() !== ""; // Check if value is not just spaces or empty
    };

    const Update = (e) => {
        e.preventDefault();
        console.log("Submitting with values:", { name, email, age });
        const trimmedName = name.trim();
        const trimmedEmail = email.trim();
        const trimmedAge = age.toString().trim();
        if (!isValidInput(name) || !isValidInput(email) || !isValidInput(age)) {
            setError("All fields are required and cannot be blank or just spaces.");
            console.log("Validation failed.");
            return; // Stop the function if validation fails
        }

        axios.put("https://merncrudbackend-1zpv.onrender.com/updateUser/" + id,{name,email,age})
        .then(result => {
            console.log(result);
            console.log("Update successful:", result.data);
            navigate('/')
        })
        .catch(err => console.log(err));
        console.error("Error updating user:");
        // console.log("the update function called:",Update);
        setError("There was an error updating the user. Please try again.");
    }

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <form onSubmit={Update}>
                    <h2>Update User</h2>
                    {error && <p className="text-danger">{error}</p>} {/* Display error if any */}
                    <div className="mb-3">
                        <label htmlFor="name">Name</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Enter Your Name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Enter Your Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="age">Age :</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Enter Your Age"
                            value={age}
                            onChange={(e) => setAge(e.target.value)}
                        />
                    </div>
                    <button className="btn btn-success">Update</button>
                </form>
            </div>
        </div>
    );
};
export default UpdateUser;
