import { createContext, useState, useEffect } from "react";
import axios from "axios";

// Create the context
export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch users data
    const fetchUsers = () => {
        setLoading(true);
        axios.get("https://merncrudbackend-1zpv.onrender.com")
            .then(result => {
                setUsers(result.data);
                setLoading(false);
            })
            .catch(err => {
                console.error(err);
                setLoading(false);
            });
    };

    // Handle delete user
    const deleteUser = (id) => {
        axios.delete(`https://merncrudbackend-1zpv.onrender.com/deleteUser/${id}`)
            .then(() => {
                setUsers(users.filter(user => user._id !== id));  // Update users state after deletion
            })
            .catch(err => console.error(err));
    };

    // Fetch users when the component mounts
    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <UserContext.Provider value={{ users, loading, fetchUsers, deleteUser }}>
            {children}
        </UserContext.Provider>
    );
};
