import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CSVImporter } from 'csv-import-react'; // Correct import for CSVImporter

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    // Update this URL to your deployed backend
    const apiUrl = "https://merncrudbackend-1zpv.onrender.com"; // Change this to your deployed URL

    useEffect(() => {
        setLoading(true);
        axios.get(`${apiUrl}/api/users`) // Update to fetch from the correct endpoint
            .then(result => {
                setUsers(result.data);
                setLoading(false);
            })
            .catch(err => {
                console.log(err);
                setLoading(false);
            });
    }, [apiUrl]);

    const handleDelete = (id) => {
        axios.delete(`${apiUrl}/api/deleteUser/${id}`) // Ensure the correct endpoint is used
            .then(res => {
                setUsers(users.filter(user => user._id !== id));
            })
            .catch(err => console.log(err));
    };

    const handleExport = async () => {
        try {
            const response = await axios.get(`${apiUrl}/api/exportUsers`, {
                responseType: 'blob', // Important for downloading files
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'export.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting data', error);
        }
    };

    const handleImport = async (data) => {
        // Convert imported data into FormData
        const formData = new FormData();
        data.forEach((item) => {
            formData.append('file', new Blob([JSON.stringify(item)], { type: 'application/json' }));
        });

        try {
            const response = await fetch(`${apiUrl}/api/importCsv`, {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Error importing CSV: ' + response.statusText);
            }

            const responseData = await response.text();
            console.log(responseData);
            // Re-fetch users after import
            fetchUsers();
        } catch (error) {
            console.error(error.message);
        }
    };

    const handleFileChange = (files) => {
        if (files.length > 0) {
            handleImport(files[0]); // Pass the first file to the handleImport
        }
    };

    const fetchUsers = async () => {
        try {
            const result = await axios.get(`${apiUrl}/api/users`);
            setUsers(result.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                
                {/* Ensure the correct component is used here */}
                <CSVImporter
                    onFileChange={handleFileChange} // Update to handle file changes
                    accept=".csv"
                />
                
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
