import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { CsvToReactTable } from "csv-import-react"; // Importing the CSV import component

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [importData, setImportData] = useState([]); // State to hold imported data

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        setLoading(true);
        try {
            const response = await axios.get('https://merncrudbackend-1zpv.onrender.com/api/users'); // Ensure this is the correct endpoint
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = (id) => {
        axios.delete('https://merncrudbackend-1zpv.onrender.com/deleteUser/' + id)
            .then(res => {
                // Update the users state to remove the deleted user
                setUsers(users.filter(user => user._id !== id));
            })
            .catch(err => console.log(err));
    };

    const handleExport = async () => {
        try {
            const response = await axios.get('https://merncrudbackend-1zpv.onrender.com/api/exportUsers', {
                responseType: 'blob', // Important for downloading files
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'export.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting data', error);
        }
    };

    const handleImport = (data) => {
        setImportData(data); // Set the imported data
        console.log(data); // For debugging purposes

        // Here you can process the data and send it to your backend if needed
        data.forEach(user => {
            axios.post('https://merncrudbackend-1zpv.onrender.com/api/users', user) // Adjust endpoint as needed
                .then(() => fetchUsers()) // Refresh the user list after import
                .catch(error => console.error('Error importing user:', error));
        });
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                {/* Button for exporting CSV */}
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                {/* CSV Import functionality */}
                <CsvToReactTable
                    onDataImport={handleImport} // Handling imported data
                    accept=".csv"
                    buttonText="Import CSV"
                    placeholder="Drag and drop or click to upload CSV file"
                />
                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
