// export default Users;
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [age, setAge] = useState('');
    const [file, setFile] = useState(null);

    useEffect(() => {
        setLoading(true);
        axios.get('https://merncrudbackend-1zpv.onrender.com')
            .then(result => {
                setUsers(result.data);
                setLoading(false);
            })
            .catch(err => {
                console.log(err);
                setLoading(false);
            });
    }, []);

    const handleDelete = (id) => {
        axios.delete('https://merncrudbackend-1zpv.onrender.com/deleteUser/' + id)
            .then(res => {
                // Update the users state to remove the deleted user
                setUsers(users.filter(user => user._id !== id));
            })
            .catch(err => console.log(err));
    };

    const handleExport = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/exportUsers', {
                responseType: 'blob', // Important for downloading files
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'users.csv');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error exporting data', error);
        }
    };

    const handleImport = async (file) => {
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('http://localhost:3001/importUsers', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Error importing CSV: ' + response.statusText);
            }

            const data = await response.text();
            console.log(data); // For debugging purposes
            alert('Users imported successfully');
        } catch (error) {
            console.error(error.message);
        }
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setFile(file); // Set the file state
            handleImport(file); // Import the file
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/createUser', {
                name,
                email,
                age,
            });
            alert('User added successfully');
            setName('');
            setEmail('');
            setAge('');
            // Optionally, fetch updated users list after adding a new user
            const result = await axios.get('https://merncrudbackend-1zpv.onrender.com');
            setUsers(result.data);
        } catch (error) {
            console.error('Error adding user', error);
        }
    };

    return (
        <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
            <div className="w-50 bg-white rounded p-3">
                <Link to="/create" className="btn btn-success">Add +</Link>
                {/* Button for exporting CSV */}
                <button className="btn btn-primary m-2" onClick={handleExport}>Export CSV</button>
                {/* Form for importing CSV */}
                <form className="m-2">
                    <input type="file" id="csvFileInput" accept=".csv" required onChange={handleFileChange} />
                    <button type="button" className="btn btn-warning m-2" onClick={() => document.getElementById('csvFileInput').click()}>Import CSV</button>
                </form>

                {/* User Form for Adding a User */}
                <h1>User Form</h1>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="number"
                        placeholder="Age"
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                    />
                    <button type="submit">Add</button>
                </form>

                {loading ? (
                    <div className="text-center">
                        <div className="spinner-border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (
                    users.length > 0 ? (
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>Age</th>
                                    <th style={{ textAlign: "center" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map(user => (
                                    <tr key={user._id}>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <Link to={`/update/${user._id}`} className="btn btn-success m-2">Update</Link>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(user._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <div className="text-center">
                            <h3>No records found</h3>
                        </div>
                    )
                )}
            </div>
        </div>
    );
};

export default Users;
